import { Button, createTheme, Tab, Tabs, TextField, ThemeProvider } from '@mui/material'
import React, { useState } from 'react'
import SearchIcon from "@mui/icons-material/Search"
import axios from 'axios'

const Search = () => {

  const [type, setType] = useState(0)
  const [page, setPage] = useState(1)
  const [searchText, setSearchText] = useState("")
  const [content, setContent] = useState()
  const [numOfPages, setNumOfPages] = useState()

  const darkTheme = createTheme({
    palette:{
      mode: "dark",
      primary:{
        main: "#fff",
      }
    }
  })

  const fetchSearch = async () => {
    const { data } = await axios.get(
      `https://api.themoviedb.org/3/search/${type ? "tv" : "movie"}?api_key=${process.env.REACT_APP_API_KEY }&language=en-US&query=${searchText}&page=${page}&include_adult=false`
    )
    setContent(data.results)
    setNumOfPages(data.total_pages)
  }

  useEffect(() => {
    window.scroll(0,0)
    fetchSearch()
  }, [type, page])



  return (
    <div>
      <ThemeProvider theme={darkTheme}>
        <div style={{display: "flex", margin: "15px 0"}}>
          <TextField
            style={{ flex: 1 }}
            className="searchBox"
            label="Search"
            variant="filled"
            onChange={(e) => setSearchText(e.target.value)}
          />
          <Button variant='contained' style={{marginLeft: 10}}> 
            <SearchIcon/> 
          </Button>
        </div>

        <Tabs 
          value={type} 
          indicatorColor='primary' 
          textColor='primary'
          onChange={(event, newValue) => {
            setType(newValue)
            setPage(1)
          }}
          style={{ paddingBottom: 5 }}
          >
          <Tab style={{ width: "50%" }} label="Search Movies"/>
          <Tab style={{ width: "50%" }} label="Search TV Series"/>
        </Tabs>

      </ThemeProvider>
    </div>
  )
}

export default Search
